/*!
 * Chirpy Theme Commons JavaScript
 * Common functionality for all pages that don't have specific layouts
 */

// Commons initialization
document.addEventListener('DOMContentLoaded', function() {
    initCommons();
});

function initCommons() {
    // Initialize basic features
    initBasicFeatures();
    
    // Initialize error page if applicable
    if (isErrorPage()) {
        initErrorPage();
    }
    
    // Initialize search if applicable
    if (isSearchPage()) {
        initSearchPage();
    }
    
    // Initialize any generic page features
    initGenericFeatures();
}

// Basic features for all pages
function initBasicFeatures() {
    initResponsiveImages();
    initExternalLinks();
    initScrollToTop();
    initKeyboardNavigation();
    initAccessibility();
}

// Responsive images
function initResponsiveImages() {
    const images = document.querySelectorAll('img');
    
    images.forEach(function(img) {
        // Add lazy loading if not already set
        if (!img.loading && img.src) {
            img.loading = 'lazy';
        }
        
        // Handle image load errors
        img.addEventListener('error', function() {
            this.style.display = 'none';
            
            // Show placeholder or alternative text
            const placeholder = document.createElement('div');
            placeholder.className = 'image-placeholder';
            placeholder.textContent = this.alt || 'Image not available';
            placeholder.style.cssText = `
                background: #f0f0f0;
                color: #666;
                padding: 20px;
                text-align: center;
                border: 1px dashed #ccc;
                margin: 10px 0;
            `;
            
            this.parentNode.insertBefore(placeholder, this.nextSibling);
        });
    });
}

// External links handling
function initExternalLinks() {
    const links = document.querySelectorAll('a[href^="http"]:not([href*="' + window.location.hostname + '"])');
    
    links.forEach(function(link) {
        // Add external link indicator
        if (!link.querySelector('.external-icon')) {
            const icon = document.createElement('i');
            icon.className = 'fas fa-external-link-alt external-icon';
            icon.style.marginLeft = '4px';
            icon.style.fontSize = '0.8em';
            link.appendChild(icon);
        }
        
        // Open in new tab
        link.target = '_blank';
        link.rel = 'noopener noreferrer';
    });
}

// Scroll to top functionality
function initScrollToTop() {
    let scrollTopBtn = document.querySelector('#scroll-top');
    
    if (!scrollTopBtn) {
        scrollTopBtn = document.createElement('button');
        scrollTopBtn.id = 'scroll-top';
        scrollTopBtn.innerHTML = '<i class="fas fa-chevron-up"></i>';
        scrollTopBtn.title = 'Back to top';
        scrollTopBtn.style.cssText = `
            position: fixed;
            bottom: 20px;
            right: 20px;
            background: rgba(0,0,0,0.7);
            color: white;
            border: none;
            border-radius: 50%;
            width: 45px;
            height: 45px;
            cursor: pointer;
            display: none;
            z-index: 1000;
            transition: all 0.3s ease;
        `;
        document.body.appendChild(scrollTopBtn);
    }
    
    window.addEventListener('scroll', function() {
        if (window.pageYOffset > 300) {
            scrollTopBtn.style.display = 'block';
        } else {
            scrollTopBtn.style.display = 'none';
        }
    });
    
    scrollTopBtn.addEventListener('click', function() {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        });
    });
}

// Keyboard navigation
function initKeyboardNavigation() {
    document.addEventListener('keydown', function(e) {
        // Skip if user is typing in an input
        if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') {
            return;
        }
        
        switch (e.key) {
            case '/':
                e.preventDefault();
                const searchInput = document.querySelector('#search-input');
                if (searchInput) {
                    searchInput.focus();
                }
                break;
                
            case 'Escape':
                // Close any open modals or overlays
                const modal = document.querySelector('.modal.show');
                if (modal) {
                    modal.classList.remove('show');
                }
                
                const overlay = document.querySelector('.image-overlay');
                if (overlay) {
                    overlay.remove();
                }
                break;
        }
    });
}

// Accessibility improvements
function initAccessibility() {
    // Add skip link if not present
    if (!document.querySelector('#skip-link')) {
        const skipLink = document.createElement('a');
        skipLink.id = 'skip-link';
        skipLink.href = '#main-content';
        skipLink.textContent = 'Skip to main content';
        skipLink.style.cssText = `
            position: absolute;
            top: -40px;
            left: 6px;
            background: #000;
            color: #fff;
            padding: 8px;
            text-decoration: none;
            z-index: 1001;
        `;
        
        skipLink.addEventListener('focus', function() {
            this.style.top = '6px';
        });
        
        skipLink.addEventListener('blur', function() {
            this.style.top = '-40px';
        });
        
        document.body.insertBefore(skipLink, document.body.firstChild);
    }
    
    // Improve focus indicators
    const focusableElements = document.querySelectorAll('a, button, input, textarea, select');
    focusableElements.forEach(function(element) {
        element.addEventListener('focus', function() {
            this.style.outline = '2px solid #007bff';
            this.style.outlineOffset = '2px';
        });
        
        element.addEventListener('blur', function() {
            this.style.outline = '';
            this.style.outlineOffset = '';
        });
    });
}

// Check if current page is an error page
function isErrorPage() {
    return document.querySelector('.error-page') || 
           document.title.includes('404') || 
           document.title.includes('Error');
}

// Error page functionality
function initErrorPage() {
    // Add some interactive elements to error pages
    const errorContainer = document.querySelector('.error-container');
    if (errorContainer) {
        // Add search suggestion
        const searchSuggestion = document.createElement('div');
        searchSuggestion.className = 'error-search';
        searchSuggestion.innerHTML = `
            <p>Try searching for what you're looking for:</p>
            <input type="text" placeholder="Search..." id="error-search">
            <button onclick="performErrorSearch()">Search</button>
        `;
        errorContainer.appendChild(searchSuggestion);
    }
    
    // Add random post suggestions
    addRandomPostSuggestions();
}

function performErrorSearch() {
    const query = document.querySelector('#error-search').value.trim();
    if (query) {
        window.location.href = `/search?q=${encodeURIComponent(query)}`;
    }
}

function addRandomPostSuggestions() {
    // This would typically fetch random posts from your site
    const suggestions = document.createElement('div');
    suggestions.className = 'random-suggestions';
    suggestions.innerHTML = `
        <h3>You might be interested in:</h3>
        <ul>
            <li><a href="/">Home Page</a></li>
            <li><a href="/categories/">Browse Categories</a></li>
            <li><a href="/tags/">Browse Tags</a></li>
            <li><a href="/archives/">Archives</a></li>
        </ul>
    `;
    
    const errorContainer = document.querySelector('.error-container');
    if (errorContainer) {
        errorContainer.appendChild(suggestions);
    }
}

// Check if current page is a search page
function isSearchPage() {
    return document.querySelector('.search-page') || 
           window.location.pathname.includes('search');
}

// Search page functionality
function initSearchPage() {
    const searchInput = document.querySelector('#search-input');
    const searchResults = document.querySelector('#search-results');
    
    if (!searchInput || !searchResults) return;
    
    // Get search query from URL
    const urlParams = new URLSearchParams(window.location.search);
    const query = urlParams.get('q');
    
    if (query) {
        searchInput.value = query;
        performSearch(query);
    }
    
    searchInput.addEventListener('input', function() {
        const newQuery = this.value.trim();
        if (newQuery.length > 2) {
            performSearch(newQuery);
            updateSearchURL(newQuery);
        }
    });
}

function performSearch(query) {
    const searchResults = document.querySelector('#search-results');
    if (!searchResults) return;
    
    searchResults.innerHTML = '<p>Searching...</p>';
    
    // Simulate search (in a real implementation, you'd search through your posts)
    setTimeout(() => {
        searchResults.innerHTML = `
            <p>Search results for "${query}":</p>
            <p class="text-muted">Search functionality requires additional setup.</p>
        `;
    }, 500);
}

function updateSearchURL(query) {
    const url = new URL(window.location);
    url.searchParams.set('q', query);
    window.history.replaceState({}, '', url);
}

// Generic features for any page
function initGenericFeatures() {
    initTooltips();
    initAnimations();
    initPrintStyles();
}

function initTooltips() {
    const tooltipElements = document.querySelectorAll('[title]');
    
    tooltipElements.forEach(function(element) {
        element.addEventListener('mouseenter', function() {
            const tooltip = document.createElement('div');
            tooltip.className = 'custom-tooltip';
            tooltip.textContent = this.title;
            tooltip.style.cssText = `
                position: absolute;
                background: rgba(0,0,0,0.8);
                color: white;
                padding: 5px 10px;
                border-radius: 4px;
                font-size: 12px;
                z-index: 1000;
                pointer-events: none;
            `;
            
            document.body.appendChild(tooltip);
            
            // Remove original title to prevent browser tooltip
            this.dataset.originalTitle = this.title;
            this.title = '';
            
            // Position tooltip
            const rect = this.getBoundingClientRect();
            tooltip.style.top = (rect.top - tooltip.offsetHeight - 5) + 'px';
            tooltip.style.left = (rect.left + rect.width / 2 - tooltip.offsetWidth / 2) + 'px';
        });
        
        element.addEventListener('mouseleave', function() {
            const tooltip = document.querySelector('.custom-tooltip');
            if (tooltip) {
                tooltip.remove();
            }
            
            // Restore original title
            if (this.dataset.originalTitle) {
                this.title = this.dataset.originalTitle;
            }
        });
    });
}

function initAnimations() {
    // Simple fade-in animation for elements
    const animateElements = document.querySelectorAll('.animate-fade-in');
    
    const observer = new IntersectionObserver(function(entries) {
        entries.forEach(function(entry) {
            if (entry.isIntersecting) {
                entry.target.style.opacity = '1';
                entry.target.style.transform = 'translateY(0)';
            }
        });
    });
    
    animateElements.forEach(function(element) {
        element.style.opacity = '0';
        element.style.transform = 'translateY(20px)';
        element.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
        observer.observe(element);
    });
}

function initPrintStyles() {
    // Add print-specific functionality
    window.addEventListener('beforeprint', function() {
        // Expand any collapsed sections for printing
        const collapsedElements = document.querySelectorAll('.collapsed');
        collapsedElements.forEach(function(element) {
            element.classList.add('expanded-for-print');
            element.classList.remove('collapsed');
        });
    });
    
    window.addEventListener('afterprint', function() {
        // Restore collapsed state after printing
        const expandedElements = document.querySelectorAll('.expanded-for-print');
        expandedElements.forEach(function(element) {
            element.classList.remove('expanded-for-print');
            element.classList.add('collapsed');
        });
    });
}

// Export for global access
window.ChirpyCommons = {
    initCommons: initCommons,
    initBasicFeatures: initBasicFeatures,
    initErrorPage: initErrorPage,
    initSearchPage: initSearchPage,
    performSearch: performSearch
};